document.querySelectorAll(".dropdown").forEach((e=>{const t=e.querySelector(".select"),c=e.querySelector(".caret"),s=e.querySelector(".menu"),l=e.querySelectorAll(".menu li"),r=e.querySelector(".selected");t.addEventListener("click",(()=>{t.classList.toggle("select-clicked"),c.classList.toggle("caret-rotate"),s.classList.toggle("menu-open")})),l.forEach((e=>{e.addEventListener("click",(()=>{r.innerText=e.innerText,t.classList.remove("select-clicked"),c.classList.remove("caret-rotate"),s.classList.remove("menu-open"),l.forEach((e=>{e.classList.remove("active")})),e.classList.add("active")}))}))}));